DROP TABLE IF EXISTS `editions`;
CREATE TABLE `editions` (`id` bigint(20) NOT NULL AUTO_INCREMENT,`name` varchar(145) NOT NULL,`desc` varchar(145) NOT NULL,PRIMARY KEY (`id`));
INSERT INTO `editions` VALUES (1,'National','National'),(2,'New York','New York'),(3,'Weekend','Weedend');
DROP TABLE IF EXISTS `podcasts`;
CREATE TABLE `podcasts` (`id` bigint(20) NOT NULL AUTO_INCREMENT,`title` varchar(145) NOT NULL,`feed` varchar(145) NOT NULL,`insertion_date` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,`description` varchar(500) DEFAULT NULL,`link_on_podcastpedia` varchar(145) DEFAULT NULL,`edition_id` bigint(20) NOT NULL,PRIMARY KEY (`id`),UNIQUE KEY FEED_UNIQUE_0 (`feed`),KEY EDITION_ID_0 (`edition_id`));
INSERT INTO `podcasts` VALUES (1,'Quarks & Co - zum Mitnehmen','http://podcast.wdr.de/quarks.xml','2014-01-09 20:21:00','Quarks & Co: Das Wissenschaftsmagazin','http://www.podcastpedia.org/podcasts/1/Quarks-Co-zum-Mitnehmen',3),(2,'- The Naked Scientists Podcast - Stripping Down Science','http://www.thenakedscientists.com/naked_scientists_podcast.xml','2014-01-09 20:21:10','The Naked Scientists flagship science show brings you a lighthearted look at the latest scientific breakthroughs, interviews with the world top scientists, answers to your science questions and science experiments to try at home.','http://www.podcastpedia.org/podcasts/792/-The-Naked-Scientists-Podcast-Stripping-Down-Science',2),(3,'NPR: TED Radio Hour Podcast','http://www.npr.org/rss/podcast.php?id=510298','2014-01-09 20:21:10','The TED Radio Hour is a journey through fascinating ideas: astonishing inventions, fresh approaches to old problems, new ways to think and create. Based on Talks given by riveting speakers on the world-renowned TED stage, each show is centered on a common theme - such as the source of happiness, crowd-sourcing innovation, power shifts, or inexplicable connections. The TED Radio Hour is hosted by Guy Raz, and is a co-production of NPR & TED. Follow the show @TEDRadioHour.','http://www.podcastpedia.org/podcasts/1183/NPR-TED-Radio-Hour-Podcast',1);
DROP TABLE IF EXISTS `sound_effects`;
CREATE TABLE `sound_effects` (`id` bigint(20) NOT NULL AUTO_INCREMENT,`podcast_id` bigint(20) NOT NULL,`path` varchar(145) NOT NULL,`desc` text NOT NULL,PRIMARY KEY (`id`),KEY PODCAST_ID_0 (`podcast_id`));
INSERT INTO `sound_effects` VALUES (1,1,'/dummy-dir/dummy-file-path-1','drumroll'),(2,2,'/dummy-dir/dummy-file-path-2','spooky'),(3,1,'/dummy-dir/dummy-file-path-3','club intro'),(4,3,'/dummy-dir/dummy-file-path-4','horn'),(5,2,'/dummy-dir/dummy-file-path-5','star wars lasers'),(6,3,'/dummy-dir/dummy-file-path-6','meow');
ALTER TABLE `podcasts` ADD CONSTRAINT `editions_ibfk_1` FOREIGN KEY (`edition_id`) REFERENCES `editions` (`id`);
ALTER TABLE `sound_effects` ADD CONSTRAINT `sound_effects_ibfk_1` FOREIGN KEY (`podcast_id`) REFERENCES `podcasts` (`id`);